import { useSortable } from "@dnd-kit/sortable";
import { CSS } from "@dnd-kit/utilities";
import { Button, CardBody } from "react-bootstrap";
import Card from "react-bootstrap/Card";

export function SortableTask(props) {
  const {
    attributes: headerAttributes,
    listeners: headerListeners,
    setNodeRef: headerSetNodeRef,
    transform: headerTransform,
    transition: headerTransition,
  } = useSortable({ id: `${props.id}-header` });

  const {
    attributes: bodyAttributes,
    listeners: bodyListeners,
    setNodeRef: bodySetNodeRef,
    transform: bodyTransform,
    transition: bodyTransition,
  } = useSortable({ id: `${props.id}-body` });

  const headerStyle = {
    transform: CSS.Transform.toString(headerTransform),
    transition: headerTransition,
  };

  const bodyStyle = {
    transform: CSS.Transform.toString(bodyTransform),
    transition: bodyTransition,
  };

  return (
    <div>
      <Card>
        <Card.Header
          {...headerAttributes}
          {...headerListeners}
          ref={headerSetNodeRef}
          style={headerStyle}
        >
          {props.task.name} - Display Order: {props.task.sort_field}
        </Card.Header>
        <CardBody
          {...bodyAttributes}
          {...bodyListeners}
          ref={bodySetNodeRef}
          style={bodyStyle}
        >
          <Button
            variant="secondary"
            onClick={(event) => {
              event.stopPropagation(); // Stop the event from propagating up to the card
              props.handleViewDetails(props.task);
            }}
          >
            View Details
          </Button>
        </CardBody>
      </Card>
    </div>
  );
}
