import { useSortable } from "@dnd-kit/sortable";
import { CSS } from "@dnd-kit/utilities";
import { Button, Form, Modal } from "react-bootstrap";
import Card from "react-bootstrap/Card";
export function SortableTask(props) {
  const { attributes, listeners, setNodeRef, transform, transition } =
    useSortable({ id: props.id });

  const style = {
    transform: CSS.Transform.toString(transform),
    transition,
  };

  return (
    <>
      <div ref={setNodeRef} style={style} {...attributes} {...listeners}>
        <Card className="m-3">
          <Card.Header>
            {props.task.name} - Display Order: {props.task.sort_field}
          </Card.Header>
          <Card.Body>
            <Button
              variant="secondary"
              onClick={() => props.handleViewDetails(props.task)}
            >
              View Details
            </Button>
          </Card.Body>
        </Card>
      </div>

      <Modal show={showModal} onHide={handleCloseModal}>
        <Modal.Header closeButton></Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group controlId="formTask">
              <Form.Label>Task Name</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter task name"
                value={taskName}
                onChange={(e) => setTaskName(e.target.value)}
              />
              <Form.Label>Task Description</Form.Label>
              <Form.Control
                as="textarea"
                placeholder="Enter task description"
                value={taskDescription}
                onChange={(e) => setTaskDescription(e.target.value)}
              />
            </Form.Group>
            <Button variant="info" onClick={handleSaveChanges}>
              Update
            </Button>
          </Form>
          <h3>Comments</h3>
          {comments.length > 0 ? (
            comments.map((comment) => (
              <div key={comment.id}>
                <p>{comment.task_comment}</p>
              </div>
            ))
          ) : (
            <p>No comments available.</p>
          )}
          <Form>
            <Form.Group controlId="formComment">
              <Form.Label>Comment on Task</Form.Label>
              <Form.Control
                as="textarea"
                placeholder="Enter a comment"
                value={newComment}
                onChange={(e) => setNewComment(e.target.value)}
              />
            </Form.Group>
            <Button variant="dark" onClick={handleAddComment}>
              Add Comment
            </Button>
          </Form>
        </Modal.Body>
      </Modal>
    </>
  );
}
