import React, { useState } from "react";
import { Button, Form, Modal } from "react-bootstrap";

export const CreateNewTask = () => {
  const [show, setShow] = useState(false);
  const [taskName, setTaskName] = useState("");
  const [taskDescription, setTaskDescription] = useState("");

  const handleShow = () => setShow(true);
  const handleClose = () => {
    setShow(false);
    setTaskName("");
    setTaskDescription("");
  };

  const handleTaskNameChange = (e) => setTaskName(e.target.value);
  const handleTaskDescriptionChange = (e) => setTaskDescription(e.target.value);

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle form submission here, e.g., send data to a server or update state
    // For now, you can just log the values to the console
    console.log("Task Name:", taskName);
    console.log("Task Description:", taskDescription);
    // Close the modal
    handleClose();
  };

  return (
    <>
      <Button className="btn btn-primary mx-auto d-block" onClick={handleShow}>
        Create New Task
      </Button>

      <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>Create Task</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form onSubmit={handleSubmit}>
            <Form.Group controlId="taskName">
              <Form.Label>Task Name</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter task name"
                value={taskName}
                onChange={handleTaskNameChange}
              />
            </Form.Group>
            <Form.Group controlId="taskDescription">
              <Form.Label>Task Description</Form.Label>
              <Form.Control
                as="textarea"
                rows={3}
                placeholder="Enter task description"
                value={taskDescription}
                onChange={handleTaskDescriptionChange}
              />
            </Form.Group>
            <Button variant="primary" type="submit">
              Save Changes
            </Button>
          </Form>
        </Modal.Body>
      </Modal>
    </>
  );
};
