import React, { useState, useEffect } from "react";
import {
  Card,
  Container,
  Row,
  Col,
  Button,
  Modal,
  Form,
} from "react-bootstrap";
import io from "socket.io-client";
import Axios from "axios";
import { useDraggable } from "@dnd-kit/core";

const socket = io("http://localhost:3001/");

export const TaskList = () => {
  const [tasks, setTasks] = useState([]);
  const [comments, setComments] = useState([]);
  const [newComment, setNewComment] = useState("");
  const [taskName, setTaskName] = useState("");
  const [taskDescription, setTaskDescription] = useState("");
  const [showModal, setShowModal] = useState(false);
  const [selectedTask, setSelectedTask] = useState(null);

  useEffect(() => {
    socket.on("tasks", (updatedTasks) => {
      setTasks(updatedTasks);
    });
  }, []);

  const loadComments = async (taskId) => {
    try {
      const response = await Axios.get(
        `http://localhost:3001/api/comments/${taskId}`
      );
      setComments(response.data);
    } catch (error) {
      console.error("Error fetching comments:", error.message);
    }
  };

  const handleViewDetails = (task) => {
    setTaskName(task.name);
    setTaskDescription(task.description);

    setSelectedTask(task);
    loadComments(task.id);
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setShowModal(false);
  };

  const handleSaveChanges = async () => {
    try {
      const response = await Axios.put(
        `http://localhost:3001/api/tasks/${selectedTask.id}`,
        {
          name: taskName,
          description: taskDescription,
        }
      );

      const updatedTask = response.data;

      setTasks((prevTasks) =>
        prevTasks.map((task) =>
          task.id === updatedTask.id ? updatedTask : task
        )
      );

      socket.emit("tasks", tasks);

      handleCloseModal();
    } catch (error) {
      console.error("Error updating task:", error.message);
    }
  };

  const handleAddComment = async () => {
    try {
      const response = await Axios.post(`http://localhost:3001/api/comments`, {
        task_comment: newComment,
        task_id: selectedTask.id,
      });

      const addedComment = response.data;

      setComments((prevComments) => [...prevComments, addedComment]);

      socket.emit("taskComments", addedComment);

      setNewComment("");
    } catch (error) {
      console.error("Error adding comment:", error.message);
    }
  };

  const { attributes, listeners, setNodeRef, transform } = useDraggable({
    id: "draggable",
  });
  const style = transform
    ? {
        transform: `translate3d(${transform.x}px, ${transform.y}px, 0)`,
      }
    : undefined;

  return (
    <div ref={setNodeRef} style={style} {...listeners} {...attributes}>
      <Container className="d-flex justify-content-center align-items-center">
        <Row className="flex-column align-items-center">
          {tasks.map((task) => (
            <Col key={task.id}>
              <Card style={{ width: "18rem" }}>
                <Card.Header>
                  {task.name} - Display Order: {task.sort_field}
                </Card.Header>
                <Card.Body>
                  <Button onClick={() => handleViewDetails(task)}>
                    View Details
                  </Button>
                </Card.Body>
              </Card>
            </Col>
          ))}
        </Row>

        <Modal show={showModal} onHide={handleCloseModal}>
          <Modal.Header closeButton></Modal.Header>
          <Modal.Body>
            <Form>
              <Form.Group controlId="formTask">
                <Form.Label>Task Name</Form.Label>
                <Form.Control
                  type="text"
                  placeholder="Enter task name"
                  value={taskName}
                  onChange={(e) => setTaskName(e.target.value)}
                />
                <Form.Label>Task Description</Form.Label>
                <Form.Control
                  as="textarea"
                  placeholder="Enter task description"
                  value={taskDescription}
                  onChange={(e) => setTaskDescription(e.target.value)}
                />
              </Form.Group>
              <Button variant="primary" onClick={handleSaveChanges}>
                Update
              </Button>
            </Form>
            <h3>Comments</h3>
            {comments.length > 0 ? (
              comments.map((comment) => (
                <div key={comment.id}>
                  <p>{comment.task_comment}</p>
                </div>
              ))
            ) : (
              <p>No comments available.</p>
            )}
            <Form>
              <Form.Group controlId="formComment">
                <Form.Label>Comment on Task</Form.Label>
                <Form.Control
                  as="textarea"
                  placeholder="Enter a comment"
                  value={newComment}
                  onChange={(e) => setNewComment(e.target.value)}
                />
              </Form.Group>
              <Button variant="secondary" onClick={handleAddComment}>
                Add Comment
              </Button>
            </Form>
          </Modal.Body>
        </Modal>
      </Container>
    </div>
  );
};
